function start(){
	var base = document.getElementById("scene");
	if (base.childElementCount == 0){
		base.innerHTML= parti["sezione1"];
	}
}
function reply(item){
	var valutac = item.attr("value");
	var base = document.getElementById("scene");
	base.innerHTML= parti[valutac];
	$.ajax({ type: "POST",   
				url: "controlB2.php",
				data: {'sezione' : valutac},
			});
}
 

$(document).ready(function() {
		$.ajax({ type: "POST",   
			url: "controlA2.php",
			success : function(risultato) {
				if (risultato != "sezione1"){
					var base = document.getElementById("scene");
					base.innerHTML = parti[risultato];
				} else {
					start();
				}
			}
		});
}); 

